SPELL_ABILITY.RESOLUTION_TIME_ACTION:
local opponent = EffectDC():Get_Targets(0):Get_PlayerPtr(0) ObjectDC():Set_PlayerPtr(0, opponent) local total = 0 local filter = Object():GetFilter() local effectController = EffectController() effectController:MarkSearchedLibrary() filter:Clear() filter:NotTargetted() filter:SetZone( ZONE_LIBRARY ) filter:SetPlayer( effectController ) total = filter:Count()
if total > 3 then effectController:SetTargetCount( 3 ) else effectController:SetTargetCount( total ) end
for i=0,total-1 do effectController:SetTargetPrompt( i, "CARD_QUERY_CHOOSE_CARD_TO_REVEAL" ) end effectController:ChooseTargets( NO_VALIDATION, EffectDC():Make_Targets(1) )
