SPELL_ABILITY.CONTINUOUS_ACTION:
local target_one_dc = EffectDC():Get_Targets(0) local target_two_dc = EffectDC():Get_Targets(1) local player_one = EffectDC():Get_PlayerPtr(2) local player_two = EffectDC():Get_PlayerPtr(3) local exchange = EffectDC():Get_Int(4) if exchange == 1 then if target_one_dc ~= nil then local target_one = target_one_dc:Get_CardPtr(0)
if target_one ~= nil and player_two ~= nil then target_one:SetController( player_two ) end end if target_two_dc ~= nil then local target_two = target_two_dc:Get_CardPtr(0)
if target_two ~= nil and player_one ~= nil then target_two:SetController( player_one ) end end end
