SPELL_ABILITY.RESOLUTION_TIME_ACTION:
local n = MTG():GetActionRepCount() local num_players = MTG():GetNumberOfPlayers() local playerindex = n local player = MTG():GetNthPlayer(playerindex) local filter = Object():GetFilter() if player ~= nil and n < num_players then -- ask the query filter:Clear() filter:NotTargetted() filter:SetPlayer( player ) filter:SetZone( ZONE_IN_PLAY ) filter:AddCardType( CARD_TYPE_CREATURE ) filter:SetHint( HINT_ENEMY, player ) player:ChooseTarget( NO_VALIDATION, "CARD_QUERY_CHOOSE_CREATURE_TO_PUT_INTO_HAND", EffectDC():Make_Targets(n) ) return true
else local i=0 for i=0,(num_players-1) do local targetDC = EffectDC():Get_Targets(i)
if targetDC ~= nil then local target_card = targetDC:Get_CardPtr(0) if target_card ~= nil then target_card:PutInHand() end end end return false end
