TRIGGERED_ABILITY.TRIGGER:
if ( MTG():GetStep() == STEP_END_OF_TURN ) then local num_players = MTG():GetNumberOfPlayers() for i=0,num_players-1 do local player = MTG():GetNthPlayer(i)
if player:GetTeam() ~= EffectController():GetTeam() and (MTG():Interrogate_LifeLostThisTurn(player)) > 2 then return true end end
end return false
