STATIC_ABILITY.FILTER:
local filteredCard = FilteredCard()
local objectPlayer = Object():GetPlayer()
return ((filteredCard ~= nil) and  filteredCard:GetPlayer() == objectPlayer and (filteredCard:GetColour():Test( COLOUR_GREEN ) ~= 0) and (filteredCard:GetCardType():Test( CARD_TYPE_CREATURE ) ~= 0) and (filteredCard:GetZone() == (ZONE_HAND)))
